mapper.select:
SELECT e.employee_id employeeId, p.program_no programNo, p.program_name programName, p.program_max_customer maxCustomer, p.program_content programContent, p.createdate createdate, p.updatedate updatedate, p.active programActive FROM program p INNER JOIN employee e ON p.employee_no = e.employee_no
AND p.program_name LIKE CONCAT('%',#{searchWord},'%')
AND p.active = #{programActive}
ORDER BY p.createdate DESC
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(DISTINCT p.program_no) FROM program p INNER JOIN employee e ON p.employee_no = e.employee_no
AND p.program_name LIKE CONCAT('%',#{searchWord},'%')
AND p.active = #{programActive}
