mapper.select:
SELECT pr.program_reservation_no programReservationNo, p.payment_date paymentDate, b.branch_name branchName, p2.program_name programName FROM program_reservation pr INNER JOIN payment p ON pr.payment_no = p.payment_no INNER JOIN program_date pd ON pr.program_date_no = pd.program_date_no LEFT JOIN program p2 ON pd.program_no = p2.program_no INNER JOIN branch b ON pr.branch_no = b.branch_no
WHERE pr.payment_no = #{paymentNo}
AND pd.program_date = #{programDate}

mapper.insert:
INSERT INTO program_reservation (
	payment_no,
	program_date_no,
	branch_no
) VALUES (
	#{paymentNo},
	#{programDateNo},
	#{branchNo}
)

INSERT INTO program_reservation (
	program_no,
	program_date,
) VALUES (
	#{programNo},
	#{programDate}
)
